package net.zdsoft.stutotality.data.service.impl;import net.zdsoft.basedata.service.impl.BaseServiceImpl;import net.zdsoft.framework.dao.BaseJpaRepositoryDao;import net.zdsoft.stutotality.data.dao.StutotalityItemOptionDao;import net.zdsoft.stutotality.data.entity.StutotalityItemOption;import net.zdsoft.stutotality.data.service.StutotalityItemOptionService;import org.apache.commons.lang3.ArrayUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.ArrayList;import java.util.List;/** * stutotality_item_option * @author * */@Service("stutotalityItemOptionService")public class StutotalityItemOptionServiceImpl extends BaseServiceImpl<StutotalityItemOption,String> implements StutotalityItemOptionService{	@Autowired	private StutotalityItemOptionDao stutotalityItemOptionDao;	@Override	protected BaseJpaRepositoryDao<StutotalityItemOption, String> getJpaDao() {		return stutotalityItemOptionDao;	}	@Override	protected Class<StutotalityItemOption> getEntityClass() {		return StutotalityItemOption.class;	}    @Override    public void saveStutotalityItemOption(StutotalityItemOption stutotalityItemOption) {        stutotalityItemOptionDao.save(stutotalityItemOption);    }    @Override    public List<StutotalityItemOption> getListByItemId(String itemId) {        return stutotalityItemOptionDao.findListByItemId(itemId);    }    @Override    public void removeStutotalityById(String id) {        stutotalityItemOptionDao.deleteStutotalityItemOptionById(id);    }    @Override    public List<StutotalityItemOption> findByItemIdsWithMaster(String[] itemIds) {        return stutotalityItemOptionDao.findByItemIds(itemIds);    }    @Override    public List<StutotalityItemOption> findListByUnitId(String unitId) {        return stutotalityItemOptionDao.findListByUnitId(unitId);    }    @Override    public List<StutotalityItemOption> findByItemIds(String[] itemIds) {	    if(ArrayUtils.isEmpty(itemIds)){	        return new ArrayList<>();        }        return stutotalityItemOptionDao.findByItemIds(itemIds);    }    @Override    public void deleteByUnitIdAndItemId(String unitId, String[] itemIds) {        stutotalityItemOptionDao.deleteByUnitIdAndItemId(unitId,itemIds);    }}