package net.zdsoft.stutotality.data.service.impl;import net.zdsoft.basedata.service.impl.BaseServiceImpl;import net.zdsoft.framework.dao.BaseJpaRepositoryDao;import net.zdsoft.stutotality.data.dao.StutotalityRewardDao;import net.zdsoft.stutotality.data.entity.StutotalityReward;import net.zdsoft.stutotality.data.service.StutotalityRewardService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * stutotality_reward  * @author  *  */@Service("stutotalityRewardService")public class StutotalityRewardServiceImpl extends BaseServiceImpl<StutotalityReward,String> implements StutotalityRewardService{	@Autowired	private StutotalityRewardDao stutotalityRewardDao;	@Override	protected BaseJpaRepositoryDao<StutotalityReward, String> getJpaDao() {		return stutotalityRewardDao;	}	@Override	protected Class<StutotalityReward> getEntityClass() {		return StutotalityReward.class;	}	@Override	public List<StutotalityReward> findByAcadyearAndSemesterAndUnitIdAndGradeId(String acadyear, String semester, String unitId, String gradeId) {		return stutotalityRewardDao.findByAcadyearAndSemesterAndUnitIdAndGradeId(acadyear, semester, unitId, gradeId);	}	@Override	public List<StutotalityReward> findByUnitId(String unitId) {		List<StutotalityReward> rewardList = stutotalityRewardDao.findByUnitId(unitId);		return rewardList;	}	@Override	public void deleteByUnitIdAndAcadyearAndSemester(String unitId, String acadyear, String semester) {			stutotalityRewardDao.deleteByUnitIdAndAcadyearAndSemester(unitId, acadyear, semester);	}    @Override	public List<StutotalityReward> findByUnitIdAndAcadyearAndSemester(String unitId, String acadyear, String semester) {		return stutotalityRewardDao.findByUnitIdAndAcadyearAndSemester(unitId, acadyear, semester);	}	@Override	public List<StutotalityReward> findByUnitIdAndAcadyearAndSemesterWithMaster(String unitId, String acadyear, String semester) {		return stutotalityRewardDao.findByUnitIdAndAcadyearAndSemester(unitId, acadyear, semester);	}}